import React, { useState, useEffect } from 'react';
import User from './User'; // Importing the User component

const UserTable = () => {
  const [users, setUsers] = useState([]);

  // Fetch users from the mock API (JSONPlaceholder)
  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => response.json())
      .then(data => setUsers(data))
      .catch(error => console.error('Error fetching data: ', error));
  }, []);

  // Delete a user
  const handleDelete = (id) => {
    setUsers(users.filter(user => user.id !== id));
    // Simulate deleting from API (you could send a DELETE request here)
  }

  return (
    <div>
      <h2>User Management</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <User key={user.id} id={user.id} name={user.name} email={user.email} onDelete={handleDelete} />
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default UserTable;
